import { AUTH, LOGOUT, SIGNIN, SIGNUP  } from "../constants/actionTypeConstants";
import * as api from '../api/api.js'


export const auth = (user) => (dispatch) => {
    try {
        const action = { type: AUTH, payload: user }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const logout = (user)=> (dispatch) => {
    try {
        const action = { type: LOGOUT, payload: user }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const signUp = (formData, history)=> async (dispatch) => {
    try {
        const {data} = await api.signUp(formData)
        const action = { type: SIGNUP, payload: data }
        dispatch(action)
        history.push('/')

    } catch (error) {
        console.log(error)


    }
}
export const signIn = (userEmail, userPassword)=> (dispatch) => {
    try {
        const action = { type: SIGNIN, payload: {userEmail, userPassword} }
        dispatch(action)
        history.push('/')

    } catch (error) {
        console.log(error)


    }
}
