import * as api from '../api/postApi'


export const getPosts = () => async (dispatch) => {
    try {
        const { data } = await api.fetchPost()
        const action = { type: 'FETCH_ALL', payload: data }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const createPost = (postData) => async (dispatch) => {
    try {
        const { data } = await api.createPost(postData)
        console.log(data)


        const action = { type: 'CREATE_POST', payload: data }
        dispatch(action)

    } catch (error) {
        console.log(error)

    }
}
export const updatePost = (postData) => async (dispatch) => {
    try {
        console.log('1')
        const { data } = await api.updatePost(postData)

        const action = { type: 'UPDATE_POST', payload: data }
        dispatch(action)

    } catch (error) {
        console.log(error)

    }
}
export const deletePost = (id) => async (dispatch) => {
    try {
        await api.deletePost(id)
        const action = { type: 'DELETE_POST', payload: id }
        dispatch(action)
    }
    catch (error) {
        console.log(error)
    }
}
