import { AUTH, LOGOUT, SIGNIN  } from "../constants/actionTypeConstants";


export const auth = (user) => (dispatch) => {
    try {
        const action = { type: AUTH, payload: user }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const logout = (user)=> (dispatch) => {
    try {
        const action = { type: LOGOUT, payload: user }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const signUpAction = (userLoginData)=> async (dispatch) => {
    try {
        const result = await api.signUp(userLoginData)
        const action = { type: SIGNUP, payload: result }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
export const signInAction = (userEmail, userPassword)=> (dispatch) => {
    try {
        const action = { type: SIGNIN, payload: {userEmail, userPassword} }
        dispatch(action)

    } catch (error) {
        console.log(error)


    }
}
