import React, { useEffect, useState } from "react";
import jwt_decode from "jwt-decode"
// import { useDispatch } from "react-redux";
import { Avatar, Button, Paper, Grid, Typography, Container, } from "@material-ui/core";
// import { useHistory } from "react-router-dom";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";

import Icon from "./icon";
// import { signin, signup } from "../../actions/auth";
// import { AUTH } from "../../constants/actionTypes";
import useStyles from "./authStyles";
import Input from "./Input";

const initialState = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: "",
};

const SignUp = () => {
  const classes = useStyles();
  const [isSignUp, setIsSignUp] = useState(true);
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    script.onload = () => {
      /* global google */
      google.accounts.id.initialize({
        client_id: '910073611932-ipqvji3ci3aog9hb0is93fpc2f9ule7r.apps.googleusercontent.com',
        callback: handleGoogleCredentialResponse,
        onError: googleError,
      });
      google.accounts.id.renderButton(document.getElementById('googleSignIn'), {
        theme: 'outline',
        size: 'large',
        buttonStyle: { width: '100%' },
      });
      // google.accounts.id.prompt();
    };
    document.head.appendChild(script);
  }, []);

  const handleChange = () => {};
  const handleSubmit = () => {};
  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };
  const handleGoogleCredentialResponse = (res) => {
   const user = jwt_decode(res.credential)
   dispatch({type:'AUTH', data:{}})
    console.log(user)
  };
  const googleError = (error) => {
    console.log(error);
  };
  const switchMode = () => {
    setIsSignUp(!isSignUp);
  };

  return (
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          {isSignUp ? "Sign up" : "Sign in"}
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {isSignUp && (
              <>
                <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half />
                <Input name="lastName" label="Last Name" handleChange={handleChange} half />
              </>
            )}
            <Input name="email" label="Email Address" handleChange={handleChange} type="email" />
            <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword} />
            {isSignUp && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />}
          </Grid>
          <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
            {isSignUp ? "Sign Up" : "Sign In"}
          </Button>
          <div id="googleSignIn" className={classes.googleButton}></div>

          <Grid container justifyContent="flex-end">
            <Grid item>
              <Button onClick={switchMode}>{isSignUp ? "Already have an account? Sign In" : "Dont have an Account? Sign Up"}</Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default SignUp;
