import React, { useState, useRef, useEffect } from 'react';
import { Typography, TextField, Button } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';

import { commentPost } from '../../actions/postsActions';
import useStyles from './postDetailsStyles';

const CommentSection = ({ post }) => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [comment, setComment] = useState('');
  const dispatch = useDispatch();
  const [updatedComments, setUpdatedComments] = useState(post?.comments);
  const { comments } = useSelector((state) => state.posts);
  const classes = useStyles();
  const commentsRef = useRef();

  useEffect(() => {
    // Concatenate the comments from the post object with the comments from the updatedComments state variable
    const allComments = post?.comments.concat(updatedComments);
    setUpdatedComments(allComments);
  }, [post, updatedComments]);

  const handleComment = async () => {
    // Dispatch the commentPost action creator and get the updated post object
    const updatedPost = await dispatch(commentPost(`${user?.result?.name}: ${comment}`, post._id));

    // Update the post object in the local state
    setUpdatedComments(updatedPost.comments);

    // Clear the comment field
    setComment('');

    // Scroll to the bottom of the comments section
    commentsRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <div className={classes.commentsOuterContainer}>
        <div className={classes.commentsInnerContainer}>
          <Typography gutterBottom variant="h6">Comments</Typography>
          {updatedComments?.map((c, i) => (
            <Typography key={i} gutterBottom variant="subtitle1">
              <strong>{c.split(': ')[0]}</strong>
              {c.split(':')[1]}
            </Typography>
          ))}
          <div ref={commentsRef} />
        </div>
        <div style={{ width: '70%' }}>
          <Typography gutterBottom variant="h6">Write a comment</Typography>
          <TextField fullWidth minRows={4} variant="outlined" label="Comment" multiline value={comment} onChange={(e) => setComment(e.target.value)} />
          <br />
          <Button style={{ marginTop: '10px' }} fullWidth disabled={!comment.length} color="primary" variant="contained" onClick={handleComment}>
            Comment
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CommentSection;
