import React, { useState } from "react";
// import { GoogleOAuthProvider } from '@react-oauth/google';
// import { GoogleLogin } from 'react-google-login';
import { GoogleLogin } from 'react-google-button';


// import { useDispatch } from "react-redux";
import { Avatar, Button, Paper, Grid, Typography, Container, } from "@material-ui/core";
// import { useHistory } from "react-router-dom";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";

import Icon from "./icon";
// import { signin, signup } from "../../actions/auth";
// import { AUTH } from "../../constants/actionTypes";
import useStyles from "./authStyles";
import Input from "./Input";

const initialState = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: "",
};

const SignUp = () => {
  const classes = useStyles();
  const [isSignUp, setIsSignUp] = useState(true);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = () => {};
  const handleSubmit = () => {};
  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };
  const googleSuccess = async (res) => {
    try {
      //    const await result = res
      console.log(res);
    } catch (error) {
      console.log(error);
      console.log("Error Logging. Please try again later");
    }
  };
  const googleError = (error) => {
    console.log(error);
  };
  const switchMode = () => {
    setIsSignUp(!isSignUp);
  };
  const GOOGLE_CLIENT_ID = '910073611932-ipqvji3ci3aog9hb0is93fpc2f9ule7r';

  return (
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          {isSignUp ? "Sign up" : "Sign in"}
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {isSignUp && (
              <>
                <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half />
                <Input name="lastName" label="Last Name" handleChange={handleChange} half />
              </>
            )}
            <Input name="email" label="Email Address" handleChange={handleChange} type="email" />
            <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword} />
            {isSignUp && (
              <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />
            )}
          </Grid>
          <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit} >
            {isSignUp ? "Sign Up" : "Sign In"}
          </Button>
          {/* <GoogleOAuthProvider clientId="910073611932-ipqvji3ci3aog9hb0is93fpc2f9ule7r.apps.googleusercontent.com">
          <GoogleLogin
            onSuccess={(credentialResponse) => {
              console.log(credentialResponse);
            }}
            onError={() => {
              console.log("Login Failed");
            }}
          />
          </GoogleOAuthProvider> */}
          {/* <GoogleLogin
            clientId="910073611932-ipqvji3ci3aog9hb0is93fpc2f9ule7r.apps.googleusercontent.com"
            render={(renderProps) => (
              <Button className={classes.googleButton} color="primary" fullWidth onClick={renderProps.onClick} disabled={renderProps.disabled} startIcon={<Icon />} variant="contained">
                Google Sign In
              </Button>
            )}
            onSuccess={googleSuccess}
            onFailure={googleError}
            cookiePolicy="single_host_origin"
          /> */}
    <GoogleLogin
        clientId="910073611932-ipqvji3ci3aog9hb0is93fpc2f9ule7r.apps.googleusercontent.com"
        onSuccess={googleSuccess}
        onFailure={googleError}
      />



          <Grid container justifyContent="flex-end">
            <Grid item>
              <Button onClick={switchMode}>
                {isSignUp
                  ? "Already have an account? Sign In"
                  : "Dont have an Account? Sign Up"}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default SignUp;
