export const postReducer = (posts = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;
        case 'CREATE_POST':
            return [...posts, action.payload]
        case 'UPDATE_POST':
            const updated_Post = posts.map((post) => post._id === action.payload._id ? action.payload : post)
            console.log(updated_Post)
            return updated_Post
        default:
            return posts;
    }
}
